sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if ((isLoading && !g_form.isNewRecord()) || (g_form.isLiveUpdating && g_form.isLiveUpdating()))
      return;

   if (newValue == '' || newValue == null) {
      g_form.setValue('u_requested_for_email', '');
      return;
   }
   if (!g_form.hasField('u_requested_for_email'))
      return;
   var caller = g_form.getReference('u_requested_for', setLocation);
}

function setLocation(caller) {
   if (caller)
       g_form.setValue('u_requested_for_email', caller.email);
}
